insert  into `hnsc_news`(`id`,`title`,`author`,`content`,`cname`,`num`,`ntime`,`flag`) values (2,'顺利中标社旗项目 公司业绩再添光彩','河南仕畅','<p style=\"line-height: 1.75em;\">&nbsp; &nbsp; 4<span style=\"font-family:宋体\">月份，社旗县李店镇李店村美丽乡村建设试点项目工程开始招标，整个项目共分为十七个标段。</span></p><p style=\"line-height: 1.75em;\"><span style=\"font-family:宋体\">&nbsp; &nbsp; 本公司得到相关讯息后，立即成立了相关的工作小组对此项目的要求进行了全面的分析和研究，最后根据各方面的情况，确定了投报第四标段的决定。</span></p><p style=\"line-height: 1.75em;\"><span style=\"font-family:宋体\">&nbsp; &nbsp; 购买回招标文件及图纸后，工作小组的相关人员认真阅读了招标文件的及施工图纸，确定了投标方向，并根据图纸的工程量清单和招标文件的相关资料制定了科学合理的工程预算造价。</span></p><p style=\"line-height: 1.75em;\"><span style=\"font-family:宋体\">&nbsp; &nbsp; 文件投标文件制作完成后，我公司法人及项目经理按时抵达社旗县公共资源交易中心，等待开标。</span></p><p style=\"line-height: 1.75em;\"><span style=\"font-family:宋体\">&nbsp; &nbsp; 经过严格的开标程序后，我公司的投标文件获得了招标人及评审专家的一致认可，顺利取得了第四标段的施工资格，为公司的业绩再添光彩！</span></p><p><br/></p>','公司新闻',68,1430319485,'y'),(3,'城市基础设施建设再添大手笔','河南仕畅','<p><span style=\"font-size: 12px;font-family: 宋体\">据不完全统计，今年全省各地初步确定城市基础设施建设项目2200多个；全年城市基础设施计划完成投资2400亿元……记者从日前召开的全省城市基础设施建设推进会上了解到，今年，我省大手笔推进城市基础设施建设，全力助推新型城镇化发展。</span></p><p><span style=\"font-size: 12px;font-family: 宋体\">&nbsp;&nbsp;&nbsp;&nbsp;</span><span style=\"font-size: 12px;font-family: 宋体\">按照省委、省政府部署，我省大力实施城镇建设扩容提升工程，城市基础设施投资逐年增加，城市综合承载能力明显提升。自2010年开展三年大提升行动计划以来，全省城镇基础设施累计完成投资8000多亿元，极大改善了城市基础设施条件。据测算，基础设施投资每增加1元，就能带动GDP增长2.4元，拉动社会投资需求3.8元。</span></p><p><span style=\"font-size: 12px;font-family: 宋体\">&nbsp;&nbsp;&nbsp;&nbsp;</span><span style=\"font-size: 12px;font-family: 宋体\">省政府出台的《关于进一步加强城镇基础设施建设管理工作的实施意见》明确指出，2015年，全省设市城市基础设施主要指标接近全国平均水平；2020年，赶上或部分超过全国平均水平。“城市基础设施不仅是城市的生命线、保障城市居民正常生产生活的基本条件，还对贯彻落实省委省政府关于稳定经济发展、遏制经济下行的决策部署，服务保增长、扩内需、调结构、促改革、惠民生的大局担当着重要作用。今年我省将通过抓项目投资、抓招商引资、抓规划引领、抓城市管理、抓责任落实等手段，掀起新一轮基础设施建设新高潮，有效改善和提升城镇居民生活水平和生活质量。”省住房和城乡建设厅厅长裴志扬表示。</span></p><p><span style=\"font-size: 12px;font-family: 宋体\">&nbsp;&nbsp;&nbsp;&nbsp;</span><span style=\"font-size: 12px;font-family: 宋体\">畅通出行方面，加快完善城市道路网络和道路网结构，规划建设一批地上、地下配套设施完善的城市主干道路、快速路和重要立体交通设施，提高道路网密度，加快支路网建设和改造。地下管廊建设方面，（下转第三版）</span></p><p><span style=\"font-size: 12px;font-family: 宋体\">&nbsp;&nbsp;&nbsp;&nbsp;</span><span style=\"font-size: 12px;font-family: 宋体\">（上接第一版）各省辖市将加快地下综合管廊试点项目建设，结合新区建设、旧城区改造、道路新（改、扩）建，在重要地段和管线密集区规划建设综合管廊。今年12个没有管道供气的县（市）将全部实现管道供气，全省实现管道燃气“县县通”目标。此外，今年全省城市将全部建成污泥集中处理设施。</span></p><p><span style=\"font-size: 12px;font-family: 宋体\">&nbsp;&nbsp;&nbsp;&nbsp;</span><span style=\"font-size: 12px;font-family: 宋体\">“疏”“堵”结合围绕“水”字做文章，也是今年我省城市基础设施建设的重要内容。我省将变过去的“以排为主”为现在的“排蓄并举”，重点解决城市排水问题，扎实推进以国家海绵型城市鹤壁市为重点的试点项目建设。针对个别城市内涝“顽疾”，通过加强城市排水防涝设施建设，今年实现消除50%以上社会影响大、群众反映强烈的重大积水内涝部位。在节水方面，积极推动安阳、新乡、濮阳、商丘、周口、登封、巩义等缺水城市创建节水型城市。</span></p><p><span style=\"font-size: 12px;font-family: 宋体\">&nbsp;&nbsp;&nbsp;&nbsp;</span><span style=\"font-size: 12px;font-family: 宋体\">城市基础设施建设最大的瓶颈是资金。为破解资金难题，省住房和城乡建设厅将积极改变过去主要依靠财政投入和城市融资平台进行社会和银行融资的模式，在全省大力推进PPP融资建设模式，实现政府融资、公共服务供给、民间资本准入的三方共赢。今年，省政府计划确定100个PPP项目，第一批总投资805亿元的29个PPP项目中，市政公用领域就占16个。</span></p><p><span style=\"font-size: 12px;font-family: 宋体\">&nbsp;&nbsp;&nbsp;&nbsp;</span><span style=\"font-size: 12px;font-family: 宋体\">省住房和城乡建设厅初步梳理2015年全省城市基础设施建设项目2200多个，全省全年城市基础设施投资计划完成2400亿元，同比增长10%左右。“时代的发展和社会的进步，促使人民群众对城市幸福生活有了新的理解，对城市人居环境有了新的期待。市民渴望出行便利，交通顺畅，雨天无积水，环境不污染，公园建到自己家门口，河水清澈见底。”裴志扬说。“随着全省城市基础设施建设加快推进，群众的渴望将逐渐变为现实。”</span></p><p><br/></p>','行业动态',10,1434121271,'y'),(4,'建筑产业现代化“中国标准”亮相','人民日报(北京)','<p style=\"margin-top:26px;margin-right:10px;margin-bottom:26px;margin-left: 0;text-align:justify;text-justify:inter-ideograph;text-indent:28px;line-height:24px\"><span style=\"font-size:14px;font-family:宋体;color:#252525;background:white\">在今天建筑产业现代化国家建筑标准设计宣贯培训会上，建筑产业现代化的“中国标准”首次亮相。</span></p><p style=\"margin-top:26px;margin-right:10px;margin-bottom:26px;margin-left: 0;text-align:justify;text-justify:inter-ideograph;text-indent:28px;line-height:24px\"><span style=\"font-size:14px;font-family:宋体;color:#252525;background:white\">据介绍，首批亮相的9项图集针对目前量大面广、问题最突出的装配式混凝土剪力墙结构住宅编制，首次形成了全国通用装配式混凝土剪力墙住宅结构体系和首套全国通用住宅系列预制构件，解决了当前行业发展的突出问题。</span></p><p style=\"margin-top:26px;margin-right:10px;margin-bottom:26px;margin-left: 0;text-align:justify;text-justify:inter-ideograph;text-indent:28px;line-height:24px\"><span style=\"font-size:14px;font-family:宋体;color:#252525;background:white\">住房城乡建设部副部长王宁表示，截至2014年底，我国以产业化方式建造的建筑面积已超过1800万平方米，预计今年底将达到5000万平方米。与此同时，从国家到地方推出各类技术标准，住宅产业化技术体系和标准逐步完善。建筑产业现代化是建筑行业转型升级的重要内容，是实施创新驱动战略的必然要求，也是工程建设领域实现可持续性发展的基本途径。</span></p><p><span style=\"font-size:14px;font-family:宋体;color:#252525;background:white\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><span style=\"font-size:14px;font-family:宋体;color:#252525;background:white\">作者：陆娅楠</span></p><p><br/></p>','行业动态',6,1434121310,'y'),(5,'建筑业粗放型发展模式进入“死亡倒计时”','中国建设报','<p style=\"text-indent:32px;line-height:200%\"><span style=\"font-size:16px;line-height:200%;font-family: 宋体\">中共中央、国务院近日印发《关于加快推进生态文明建设的意见》</span><span style=\"font-size:16px;line-height:200%\">(</span><span style=\"font-size:16px;line-height:200%;font-family:宋体\">以下简称“《意见》”</span><span style=\"font-size:16px;line-height:200%\">)</span><span style=\"font-size:16px;line-height:200%;font-family:宋体\">，就生态文明建设作出全面专题部署。从建筑业视角来看，《意见》通篇贯穿的“绿水青山就是金山银山”的理念和生产方式必须实现绿色化的要求，都意味着建筑业必须加快改变粗放型发展模式，向生产方式“绿色化”转变。</span><span style=\"font-size:16px;line-height:200%\"> </span></p><p style=\"text-indent:32px;line-height:200%\"><span style=\"font-size:16px;line-height:200%;font-family: 宋体\">《意见》明确将“绿色化”上升到与新型工业化、信息化、城镇化和农业现代化同等高度，同步推进。绿色化就是在价值观念上尊重自然、顺应自然，实现人与自然的和谐</span><span style=\"font-size:16px;line-height:200%\">;</span><span style=\"font-size:16px;line-height:200%;font-family:宋体\">在生产和生活方式上要循环、绿色、低碳</span><span style=\"font-size:16px;line-height:200%\">;</span><span style=\"font-size:16px;line-height:200%;font-family:宋体\">在体制机制上，将资源节约、污染控制和生态保护作为出发点和落脚点。绿色、循环、低碳是实现绿色化、建设生态文明的基本途径。</span><span style=\"font-size:16px;line-height:200%\"> </span></p><p style=\"text-indent:32px;line-height:200%\"><span style=\"font-size:16px;line-height:200%;font-family: 宋体\">据统计，在全社会能耗中，建筑能耗占到近一半，为</span><span style=\"font-size:16px;line-height:200%\">46.7%</span><span style=\"font-size:16px;line-height:200%;font-family:宋体\">。建筑业物质资源消耗占钢材的</span><span style=\"font-size:16px;line-height:200%\">55%</span><span style=\"font-size:16px;line-height:200%;font-family:宋体\">、木材的</span><span style=\"font-size:16px;line-height:200%\">40%</span><span style=\"font-size:16px;line-height:200%;font-family:宋体\">、水泥的</span><span style=\"font-size:16px;line-height:200%\">70%</span><span style=\"font-size:16px;line-height:200%;font-family:宋体\">、玻璃的</span><span style=\"font-size:16px;line-height:200%\">76%</span><span style=\"font-size:16px;line-height:200%;font-family:宋体\">、塑料的</span><span style=\"font-size:16px;line-height:200%\">25%</span><span style=\"font-size:16px;line-height:200%;font-family:宋体\">、运输量的</span><span style=\"font-size:16px;line-height:200%\">28%</span><span style=\"font-size:16px;line-height:200%;font-family:宋体\">。这些材料的生产需要冶炼、熔融、烧结大量的金属和非金属矿物原料、化工原料，因而建筑业也间接消耗了大量的矿产和土地资源。在加快推进生态文明建设中，建筑业首当其冲要承担这样的使命，加快转变发展方式。但从建筑业的产业地位和行业特征来看，走绿色发展、低碳发展、循环发展之路的任务却颇为艰巨。一是建筑业不仅是国民经济支柱产业，更是基础产业和富民产业。据国家统计局数据显示，</span><span style=\"font-size:16px;line-height:200%\">2013</span><span style=\"font-size:16px;line-height:200%;font-family:宋体\">年，建筑业从业人员达</span><span style=\"font-size:16px;line-height:200%\">4500</span><span style=\"font-size:16px;line-height:200%;font-family:宋体\">万人，建筑业企业</span><span style=\"font-size:16px;line-height:200%\">79000</span><span style=\"font-size:16px;line-height:200%;font-family:宋体\">余家，完成总产值</span><span style=\"font-size:16px;line-height:200%\">15.9</span><span style=\"font-size:16px;line-height:200%;font-family:宋体\">万亿元，占</span><span style=\"font-size:16px;line-height:200%\">GDP</span><span style=\"font-size:16px;line-height:200%;font-family:宋体\">比例约</span><span style=\"font-size:16px;line-height:200%\">7%</span><span style=\"font-size:16px;line-height:200%;font-family:宋体\">。这也导致了行业一直处于粗放型发展状态，转型升级步伐沉重。二是建筑业关联上下游</span><span style=\"font-size:16px;line-height:200%\">50</span><span style=\"font-size:16px;line-height:200%;font-family:宋体\">多个相关产业发展，牵一发而动全身。</span><span style=\"font-size:16px;line-height:200%\"> </span></p><p style=\"text-indent:32px;line-height:200%\"><span style=\"font-size:16px;line-height:200%\">2013</span><span style=\"font-size:16px;line-height:200%;font-family:宋体\">年年初，国务院转发发改委和住房城乡建设部《绿色建筑行动方案》的通知，要求在城镇新建建筑和既有建筑改造中“严格落实强制性节能标准”，并设定了“十二五”期间的量化目标：“十二五”期间，完成新建绿色建筑</span><span style=\"font-size:16px;line-height:200%\">10</span><span style=\"font-size:16px;line-height:200%;font-family:宋体\">亿平方米，到</span><span style=\"font-size:16px;line-height:200%\">2015</span><span style=\"font-size:16px;line-height:200%;font-family:宋体\">年末，</span><span style=\"font-size:16px;line-height:200%\">20%</span><span style=\"font-size:16px;line-height:200%;font-family:宋体\">的城镇新建建筑达到绿色建筑标准要求。而从</span><span style=\"font-size:16px;line-height:200%\">2014</span><span style=\"font-size:16px;line-height:200%;font-family:宋体\">年起，国家在部分符合条件的城市率先启动了绿色保障房行动，以此带动全国建筑执行绿色建筑标准，到</span><span style=\"font-size:16px;line-height:200%\">2020</span><span style=\"font-size:16px;line-height:200%;font-family:宋体\">年全国城镇保障房中执行绿色建筑标准的比例将超过</span><span style=\"font-size:16px;line-height:200%\">70%</span><span style=\"font-size:16px;line-height:200%;font-family:宋体\">。截至</span><span style=\"font-size:16px;line-height:200%\">2014</span><span style=\"font-size:16px;line-height:200%;font-family:宋体\">年</span><span style=\"font-size:16px;line-height:200%\">1</span><span style=\"font-size:16px;line-height:200%;font-family:宋体\">月，全国已评出</span><span style=\"font-size:16px;line-height:200%\">1446</span><span style=\"font-size:16px;line-height:200%;font-family:宋体\">项绿色建筑标识项目，总建筑面积达到</span><span style=\"font-size:16px;line-height:200%\">16290</span><span style=\"font-size:16px;line-height:200%;font-family:宋体\">万平方米，其中设计标识</span><span style=\"font-size:16px;line-height:200%\">1342</span><span style=\"font-size:16px;line-height:200%;font-family:宋体\">项，建筑面积为</span><span style=\"font-size:16px;line-height:200%\">15014.17</span><span style=\"font-size:16px;line-height:200%;font-family:宋体\">万平方米</span><span style=\"font-size:16px;line-height:200%\">;</span><span style=\"font-size:16px;line-height:200%;font-family:宋体\">运行标识</span><span style=\"font-size:16px;line-height:200%\">104</span><span style=\"font-size:16px;line-height:200%;font-family:宋体\">项，建筑面积</span><span style=\"font-size:16px;line-height:200%\">1276.07</span><span style=\"font-size:16px;line-height:200%;font-family:宋体\">万平方米。由政府推动的绿色建筑行动已在各地全面开展，建筑业企业如何从低端、高耗、产能过剩的发展模式向绿色、低碳的发展模式转型已经不是要不要的问题，而是如何转、如何更快地赶上先行者的问题。</span><span style=\"font-size:16px;line-height:200%\"> </span></p><p><span style=\"font-size:16px;font-family:宋体\">《意见》的发布，让建筑业在转型升级的道路上又向前迈进了一大步。在转型中，企业要围绕绿色建造、绿色施工、绿色建筑作文章。绿色建造，即在规划、设计阶段处理好建筑与自然的关系，使建筑与自然和谐共融。绿色施工，即要大力推行建筑工业化，这是实现绿色发展和循环经济的重要方式。绿色建筑，即在建筑的全寿命周期内最大限度地节约资源、保护环境和减少污染、与自然和谐共生。而在此之前，树立绿色化发展新思维，将绿色化与企业发展实际相结合，调整企业结构，则是企业面临的首要任务。</span></p><p><br/></p>','行业动态',4,1434121368,'y'),(6,'住房城乡建设部修改13项部门规章','《中国建设报》','<p><span style=\"font-size: 18px;font-family: &#39;宋体 ，Arial&#39;\">多处注册资本金的要求被删除</span></p><p style=\"line-height:24px\"><span style=\"font-size: 12px;font-family: &#39;宋体 ，Arial&#39;\">日前，住房城乡建设部部长陈政高签署决定，对《房地产开发企业资质管理规定》等部门规章进行修改，并已开始施行。</span></p><p style=\"line-height:24px\"><span style=\"font-size: 12px;font-family: &#39;宋体 ，Arial&#39;\">　　此次修改根据为《公司法》，修改范围包括《房地产开发企业资质管理规定》、《市政公用事业特许经营管理办法》、《城市房屋白蚁防治管理规定》、《建设工程质量检测管理办法》、《工程造价咨询企业管理办法》、《工程建设项目招标代理机构资格认定办法》、《城市生活垃圾管理办法》、《工程监理企业资质管理规定》、《建设工程勘察设计资质管理规定》、《物业服务企业资质管理办法》、《城乡规划编制单位资质管理规定》、《房屋建筑和市政基础设施工程施工图设计文件审查管理办法》、《房地产估价机构管理办法》共13项部门规章。</span></p><p style=\"line-height:24px\"><span style=\"font-size: 12px;font-family: &#39;宋体 ，Arial&#39;\">　　此次修改涉及房地产开发、市政公用、建设工程等多个领域，放宽了对注册资本金的硬性要求，其中仅删除“注册资本金不少于XXX万元”或“XXX万元以上”的修改就达15处。</span></p><p style=\"line-height:24px\"><span style=\"font-size: 12px;font-family: &#39;宋体 ，Arial&#39;\">　　决定最后明确，该决定自发布之日（5月4日）起施行。所涉及部门规章根据该决定作相应修改，重新发布。</span></p><p><span style=\"font-size: 12px;font-family: &#39;宋体 ，Arial&#39;\">摘自　《中国建设报》　2015.05.21　宗边</span></p><p><br/></p>','行业动态',9,1434121412,'y'),(7,'走进南太行•畅游八里沟','河南仕畅','<p style=\"margin-top:10px;margin-right:0;margin-bottom:0;margin-left:0;margin-bottom:0;text-align:center;line-height:25px;background:white\"><strong><span style=\"font-family: &#39;Helvetica Neue&#39;\">——</span></strong><strong><span style=\"font-family: &#39;Helvetica Neue&#39;\">清华大学继续教育学院国际高级工商管理总裁研修班学员联谊会圆满成功</span></strong></p><p style=\"margin-top:10px;margin-right:0;margin-bottom:0;margin-left:0;margin-bottom:0;line-height:25px;background:white\"><span style=\"font-family: &#39;Helvetica Neue&#39;;color: rgb(62, 62, 62)\">&nbsp;</span></p><p style=\"margin-top:10px;margin-right:0;margin-bottom:0;margin-left:0;margin-bottom:0;line-height:25px;background:white\"><span style=\"font-size: 12px;font-family: &#39;Helvetica Neue&#39;\">2014</span><span style=\"font-size: 12px;font-family: &#39;Helvetica Neue&#39;\">年9月20-21日，清华大学继续教育学院国际高级工商管理总裁研修班学员联谊会于新乡辉县顺利召开。本次活动由清华大学继续教育学院国际高级工商管理总裁研修班文艺委员河南巨星集团董事长牛献忠全程赞助，班主任薛国霞女士、副班长河南仕畅市政工程有限公司董事长熊文强以及各位班级委员、班级学员都参与了本次活动。</span></p><p style=\"margin-top:10px;margin-right:0;margin-bottom:0;margin-left:0;margin-bottom:0;line-height:25px;background:white\"><span style=\"font-size: 12px;font-family: &#39;Helvetica Neue&#39;\">活动得到了河南巨星集团全员的高度重视，并举行了隆重的欢迎仪式！欢迎仪式结束后，学员们在牛总的带领下对巨星集团进行了参观，参观过程中，学员们均被巨星集团浓厚的文化氛围所折服。随后，牛献忠牛总将巨星集团高管向学员们进行一一介绍，每一个巨星人所散发的正能量都使到场的学员们倍感亲切。</span></p><p style=\"text-align: center;\"><img src=\"/upload/image/20150612/143412191010203265.jpg\" alt=\"143412191010203265.jpg\"/></p>_ueditor_page_break_tag_<p style=\"text-align: center;\"><img src=\"/upload/image/20150612/143412191813011089.jpg\" alt=\"143412191813011089.jpg\"/></p><p><span style=\"font-size:12px;font-family: 宋体;color:#444444\"><br/></span></p><p><span style=\"font-size:12px;font-family: 宋体;color:#444444\">20</span><span style=\"font-size:12px;font-family:宋体;color:#444444\">日下午，学员们一行来到了天界山，目不暇接的自然风光使得学员们心旷神怡，无不感叹大自然的鬼斧神工。欣赏美景的同时，牛总还为学员们静心准备了一场别开生面的“大自然合唱团”，合唱团全体成员高亢的歌声回响山谷，美妙的歌声与大自然的风景浑然天成！</span></p>_ueditor_page_break_tag_<p><span style=\"font-size:12px;font-family:宋体;color:#444444\"><br/></span></p><p style=\"text-align: center;\"><img src=\"/upload/image/20150612/143412190198973166.jpg\" style=\"\"/></p><p style=\"text-align: center;\"><img src=\"/upload/image/20150612/143412190143505130.jpg\" style=\"\"/></p>_ueditor_page_break_tag_<p><span style=\"background-color: white; font-family: &#39;Helvetica Neue&#39;; font-size: 9pt;\"><br/></span></p><p><span style=\"background-color: white; font-family: &#39;Helvetica Neue&#39;; font-size: 9pt;\">夜晚，学员们迎来了狂欢派对，熊熊的篝火，爽朗的笑声，学员们尽情放松，大声歌唱。</span><br/></p><p><span style=\"font-size: 9pt; font-family: &#39;Helvetica Neue&#39;; background-color: white; background-position: initial initial; background-repeat: initial initial;\"><br/></span></p><p style=\"text-align: center;\"><img src=\"/upload/image/20150612/143412190141839801.jpg\" style=\"\"/></p><p><br/></p>','公司新闻',114,1411312290,'y'),(9,'走进天中之中，乐享同窗深情——清大总裁班企业家莅临我公司参观','河南仕畅','<p style=\"TEXT-ALIGN: left; PADDING-BOTTOM: 0px; TEXT-TRANSFORM: none; TEXT-INDENT: 20px; MARGIN: 0px; PADDING-LEFT: 0px; WIDTH: 723px; PADDING-RIGHT: 0px; FONT: 12px/24px 宋体; WHITE-SPACE: normal; LETTER-SPACING: normal; HEIGHT: 48px; COLOR: rgb(68,68,68); WORD-SPACING: 0px; PADDING-TOP: 0px; -webkit-text-stroke-width: 0px\">走进天中之中，乐享同窗深情——清华大学总裁班第14期同学企业互访第一期走进河南仕畅市政工程有限公司于2015年6月10-11日在驻马店顺利举行。为期两天的班级聚会在同学们的欢乐笑语中度过，同学们畅聊同窗之情，彼此间的深厚情感逐步加深。</p><p style=\"TEXT-ALIGN: left; PADDING-BOTTOM: 0px; TEXT-TRANSFORM: none; TEXT-INDENT: 20px; MARGIN: 0px; PADDING-LEFT: 0px; WIDTH: 720px; PADDING-RIGHT: 0px; FONT: 12px/24px 宋体; WHITE-SPACE: normal; LETTER-SPACING: normal; HEIGHT: 48px; COLOR: rgb(68,68,68); WORD-SPACING: 0px; PADDING-TOP: 0px; -webkit-text-stroke-width: 0px\">同学们首先对熊班长企业河南仕畅市政工程有限公司进行了参观，随后参观盘古圣地，攀爬灵秀山岳，一路欢歌中本次活动得以圆满成功！</p><p style=\"TEXT-ALIGN: center; PADDING-BOTTOM: 0px; TEXT-TRANSFORM: none; TEXT-INDENT: 20px; MARGIN: 0px; PADDING-LEFT: 0px; WIDTH: 750px; PADDING-RIGHT: 0px; FONT: 12px/24px 宋体; WHITE-SPACE: normal; LETTER-SPACING: normal; COLOR: rgb(68,68,68); WORD-SPACING: 0px; PADDING-TOP: 0px; -webkit-text-stroke-width: 0px\"><img style=\"BORDER-BOTTOM: 0px; BORDER-LEFT: 0px; PADDING-BOTTOM: 0px; MARGIN: 0px; PADDING-LEFT: 0px; PADDING-RIGHT: 0px; BORDER-TOP: 0px; BORDER-RIGHT: 0px; PADDING-TOP: 0px\" alt=\"\" src=\"http://www.qingdaqiye.com/img/editor/201506/sm/12150031_3751.jpg\"/></p><p style=\"TEXT-ALIGN: center; PADDING-BOTTOM: 0px; TEXT-TRANSFORM: none; TEXT-INDENT: 20px; MARGIN: 0px; PADDING-LEFT: 0px; WIDTH: 750px; PADDING-RIGHT: 0px; FONT: 12px/24px 宋体; WHITE-SPACE: normal; LETTER-SPACING: normal; COLOR: rgb(68,68,68); WORD-SPACING: 0px; PADDING-TOP: 0px; -webkit-text-stroke-width: 0px\">&nbsp;</p><p style=\"TEXT-ALIGN: center; PADDING-BOTTOM: 0px; TEXT-TRANSFORM: none; TEXT-INDENT: 20px; MARGIN: 0px; PADDING-LEFT: 0px; WIDTH: 750px; PADDING-RIGHT: 0px; FONT: 12px/24px 宋体; WHITE-SPACE: normal; LETTER-SPACING: normal; COLOR: rgb(68,68,68); WORD-SPACING: 0px; PADDING-TOP: 0px; -webkit-text-stroke-width: 0px\"><img style=\"BORDER-BOTTOM: 0px; BORDER-LEFT: 0px; PADDING-BOTTOM: 0px; MARGIN: 0px; PADDING-LEFT: 0px; PADDING-RIGHT: 0px; BORDER-TOP: 0px; BORDER-RIGHT: 0px; PADDING-TOP: 0px\" alt=\"\" src=\"http://www.qingdaqiye.com/img/editor/201506/sm/12150043_1877.jpg\"/></p><p style=\"TEXT-ALIGN: center; PADDING-BOTTOM: 0px; TEXT-TRANSFORM: none; TEXT-INDENT: 20px; MARGIN: 0px; PADDING-LEFT: 0px; WIDTH: 750px; PADDING-RIGHT: 0px; FONT: 12px/24px 宋体; WHITE-SPACE: normal; LETTER-SPACING: normal; COLOR: rgb(68,68,68); WORD-SPACING: 0px; PADDING-TOP: 0px; -webkit-text-stroke-width: 0px\">&nbsp;</p><p style=\"TEXT-ALIGN: center; PADDING-BOTTOM: 0px; TEXT-TRANSFORM: none; TEXT-INDENT: 20px; MARGIN: 0px; PADDING-LEFT: 0px; WIDTH: 750px; PADDING-RIGHT: 0px; FONT: 12px/24px 宋体; WHITE-SPACE: normal; LETTER-SPACING: normal; COLOR: rgb(68,68,68); WORD-SPACING: 0px; PADDING-TOP: 0px; -webkit-text-stroke-width: 0px\"><img style=\"BORDER-BOTTOM: 0px; BORDER-LEFT: 0px; PADDING-BOTTOM: 0px; MARGIN: 0px; PADDING-LEFT: 0px; PADDING-RIGHT: 0px; BORDER-TOP: 0px; BORDER-RIGHT: 0px; PADDING-TOP: 0px\" alt=\"\" src=\"http://www.qingdaqiye.com/img/editor/201506/sm/12150055_2348.jpg\"/></p><p style=\"TEXT-ALIGN: center; PADDING-BOTTOM: 0px; TEXT-TRANSFORM: none; TEXT-INDENT: 20px; MARGIN: 0px; PADDING-LEFT: 0px; WIDTH: 750px; PADDING-RIGHT: 0px; FONT: 12px/24px 宋体; WHITE-SPACE: normal; LETTER-SPACING: normal; COLOR: rgb(68,68,68); WORD-SPACING: 0px; PADDING-TOP: 0px; -webkit-text-stroke-width: 0px\">&nbsp;</p><p style=\"TEXT-ALIGN: center; PADDING-BOTTOM: 0px; TEXT-TRANSFORM: none; TEXT-INDENT: 20px; MARGIN: 0px; PADDING-LEFT: 0px; WIDTH: 750px; PADDING-RIGHT: 0px; FONT: 12px/24px 宋体; WHITE-SPACE: normal; LETTER-SPACING: normal; COLOR: rgb(68,68,68); WORD-SPACING: 0px; PADDING-TOP: 0px; -webkit-text-stroke-width: 0px\"><img style=\"BORDER-BOTTOM: 0px; BORDER-LEFT: 0px; PADDING-BOTTOM: 0px; MARGIN: 0px; PADDING-LEFT: 0px; PADDING-RIGHT: 0px; BORDER-TOP: 0px; BORDER-RIGHT: 0px; PADDING-TOP: 0px\" alt=\"\" src=\"http://www.qingdaqiye.com/img/editor/201506/sm/12150106_3755.jpg\"/></p><p>&nbsp;</p>','公司新闻',72,1434293145,'y');

/*Table structure for table `hnsc_newsclass` */

DROP TABLE IF EXISTS `hnsc_newsclass`;

CREATE TABLE `hnsc_newsclass` (
  `cname` varchar(50) NOT NULL DEFAULT '',
  PRIMARY KEY (`cname`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

/*Data for the table `hnsc_newsclass` */

insert  into `hnsc_newsclass`(`cname`) values ('公司新闻'),('文件通知'),('行业动态');

/*Table structure for table `hnsc_project` */

DROP TABLE IF EXISTS `hnsc_project`;

CREATE TABLE `hnsc_project` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `title` varchar(50) DEFAULT NULL,
  `img` varchar(200) DEFAULT NULL,
  `content` text,
  `num` int(10) unsigned DEFAULT '0',
  `ntime` int(10) unsigned DEFAULT NULL,
  `jflag` enum('y','n') DEFAULT 'n',
  `flag` enum('y','n') DEFAULT 'y',
  PRIMARY KEY (`id`)
